module.exports = function(iterable, fn, context) {
	var hasOwnProperty = Object.prototype.hasOwnProperty;
	context = context || this;
	if (Object.prototype.toString.call(iterable) === '[object Object]') {
		for (var property in iterable) {
			if (hasOwnProperty.call(iterable, property)) {
				fn.call(context, property, iterable[property]);
			}
		}
	} else {
		for (var i = 0, l = iterable.length; i < l; i++) {
			fn.call(context, iterable[i], i);
		}
	}
};